import {
    GET_POSTS,
    ADD_POST,
  } from "../actions/posts";

const initialState = {
    posts:[],
    loading:true
}

export default function(state=initialState,action){
    const {type,payload} = action

    switch(type){
        case GET_POSTS:
            return {
              ...state,
              posts: payload,
              loading:false
            }
        case POSTS_ERROR:
            return {
              ...state,
              loading:false
            }
        default:
            return state;
    }
}
