import { BottomNavigationAction } from "@material-ui/core";
import {
    GET_POSTS,
    ADD_POST,
  } from "../actions/projects";

const initialState = {
    posts:[]
}

export default function(state=initialState,action){
    const {type,payload} = action

    switch(type){
        case GET_PROJECTS:
            return {
              ...state,
              posts: payload,

            };
    }
}
