import {
    GET_POSTS,
    ADD_POST,
  } from "../actions/posts";

const initialState = {
    posts:[]
}

export default function(state=initialState,action){
    const {type,payload} = action

    switch(type){
        case GET_POSTS:
            return {
              ...state,
              posts: payload,
            }
        default:
            return state;
    }
}
